
public class JavaBasic1_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int nVar=12;
		float fVar=3.14f;
		
		System.out.println(nVar);
		System.out.println(fVar);
		System.out.println();
		System.out.println("정수: "+nVar+" 부동소수: " +fVar);
		System.out.printf("정수: %1$d 부동소수 %2$f", nVar, fVar);
	}

}
